import React, { Component } from 'react'
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import './style.css'
export default class Login extends Component {
  state = {
    email: "",
    password: ""
  }

  handleChange(e){
    this.setState({
        [e.target.name] : e.target.value
    })
  }

  handleClick(){
    const { history } = this.props;
    axios.get('http://localhost:5000/users/check-user/', {
      email: this.state.email,
      password: this.state.password
    }).then((result) => {
      if(result.status === 200){
      // console.log('grreaaaaaat', result);
      return history.push('/articles')
      }
      // console.log('got', result);
    }).catch((err) => {
      console.log('err in fetching user data: ', err);
    })
  }

  render(){
    const history = useHistory();
    return(
      <div className="Login-page__div">
        <label htmlFor="email" className="Login-page__label">
          Email
        </label>
        <input
          name="email"
          placeholder=""
          className="Login-page__input"
          onChange={this.handleChange.bind(this)}
        />

        <label htmlFor="password" className="Login-page__label">
          password
        </label>
        <input
          name="password"
          placeholder=""
          className="Login-page__input"
          onChange={this.handleChange.bind(this)}
        />

        <button className="Login-page__button" onClick={this.handleClick.bind(this)}>
          login
        </button>

      </div>
    )
  }
}
